import React from 'react';
import { Link, useLocation } from "react-router-dom";
import logoDuoc from "../assets/DuocUCLogo.png";

const Item = ({ to, children, active }) => (
  <Link
    to={to}
    className={[
      "w-full rounded-md px-4 py-3 text-left shadow-md transition-colors select-none",
      active
        ? "bg-duocamarillo text-duocgris"
        : "bg-duocgris hover:bg-duocamarillo hover:text-duocgris",
      "focus:outline-none focus:ring-2 focus:ring-white/50"
    ].join(" ")}
  >
    {children}
  </Link>
);

function Navbar() {
  return (

     <aside className="fixed left-0 top-0 h-screen w-64 bg-duocazul text-white p-4 z-50">
      
    <div className="flex flex-col h-full">
      {/* Imagen en la parte superior */}
      <div className="flex justify-center mb-6">
        <img 
          src={logoDuoc}
          alt="Logo" 
          className="full" 
        />
      </div>

      {/* Contenedor de los botones */}
      <nav className="flex-1 overflow-y-auto">
          <div className="flex flex-col space-y-3">
            <Item to="/cursos" active={pathname === "/cursos"}>Mis Cursos</Item>
            <Item to="/explorar" active={pathname === "/explorar"}>Explorar cursos</Item>
            <Item to="/evaluaciones" active={pathname === "/evaluaciones"}>Evaluaciones</Item>
            <Item to="/certificados" active={pathname === "/certificados"}>Certificados</Item>
            <Item to="/calificaciones" active={pathname === "/calificaciones"}>Calificaciones</Item>
          </div>
        </nav>

      
      <div className="mt-auto pt-4 border-t border-white/10">
          <button className="w-full rounded-md bg-duocamarillo text-duocgris font-semibold px-3 py-4 hover:bg-duocceleste hover:text-white transition-colors">
            Cerrar sesi√≥n
          </button>
        </div>
    </div>
    </aside>
  );
}

export default Navbar;
