export default function Navbar() {
  return (
    <aside className="fixed left-0 top-0 h-screen w-64 bg-duocgris text-white p-4 z-50">
      {/* Wrapper en columna ocupando toda la altura */}
      <div className="flex flex-col h-full">
        {/* Imagen / Branding */}
        <div className="flex justify-center mb-6">
          <img src="/logo.png" alt="Logo" className="w-20 h-20 rounded-full object-cover" />
        </div>

        {/* Menú (si crece mucho, que scrollee) */}
        <nav className="flex-1 overflow-y-auto">
          <ul className="space-y-2">
            <li><button className="w-full rounded-md px-3 py-2 text-left hover:bg-white/10">Inicio</button></li>
            <li><button className="w-full rounded-md px-3 py-2 text-left hover:bg-white/10">Tareas</button></li>
            <li><button className="w-full rounded-md px-3 py-2 text-left hover:bg-white/10">Cursos</button></li>
            <li><button className="w-full rounded-md px-3 py-2 text-left hover:bg-white/10">Mensajes</button></li>
            <li><button className="w-full rounded-md px-3 py-2 text-left hover:bg-white/10">Calificaciones</button></li>
          </ul>
        </nav>

        {/* Botón fijo al fondo */}
        <div className="mt-auto pt-3 border-t border-white/10">
          <button className="w-full rounded-md bg-duocamarillo text-duocgris font-semibold px-3 py-2 hover:bg-duocceleste hover:text-white transition-colors">
            Cerrar sesión
          </button>
        </div>
      </div>
    </aside>
  );
}
