import React from 'react';
import logoDuoc from "../assets/DuocUCLogo.png";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Navbar() {
  const baseBtn = "w-full rounded-md px-4 py-3 shadow-md transition-colors";
  const activeBtn = "bg-duocamarillo text-duocgris";
  const inactiveBtn = "bg-duocgris hover:bg-duocamarillo hover:text-duocgris";
  const navigate = useNavigate();
  return (

     <aside className="fixed left-0 top-0 h-screen w-64 bg-duocazul text-white p-4 z-50">
      
    <div className="flex flex-col h-full">
      {/* Imagen en la parte superior */}
      <div className="flex justify-center mb-6">
        <img 
          src={logoDuoc}
          alt="Logo" 
          className="full" 
        />
      </div>

      {/* Contenedor de los botones */}
      <div className="flex flex-col space-y-4">
        {/* Botones cuadrados */}
        <button onClick={() => navigate("/")} className="bg-duocgris p-4 rounded-md shadow-md hover:bg-duocamarillo hover:text-duocgris">
          Mis Cursos
        </button>
        <button onClick={() => navigate("/ExplorarCursos")} className="bg-duocgris p-4 rounded-md shadow-md hover:bg-duocamarillo hover:text-duocgris">
          Explorar cursos
        </button>
        <button onClick={() => navigate("/Evaluaciones")} className="bg-duocgris p-4 rounded-md shadow-md hover:bg-duocamarillo hover:text-duocgris">
          Evaluaciones
        </button>
        <button onClick={() => navigate("/Certificados")} className="bg-duocgris p-4 rounded-md shadow-md hover:bg-duocamarillo hover:text-duocgris">
          Certificados
        </button>
        <button onClick={() => navigate("/Calificaciones")} className="bg-duocgris p-4 rounded-md shadow-md hover:bg-duocamarillo hover:text-duocgris">
          Calificaciones
        </button>
        
       <NavLink
  to="/certificados"
  className={({ isActive }) => `${baseBtn} ${isActive ? activeBtn : inactiveBtn}`}
>
  Certificados
</NavLink>
    
      </div>
      

      
      <div className="mt-auto pt-4 border-t border-white/10">
          <button className="w-full rounded-md bg-duocamarillo text-duocgris font-semibold px-3 py-4 hover:bg-duocceleste hover:text-white transition-colors">
            Cerrar sesi√≥n
          </button>
        </div>
    </div>
    </aside>
  );
}

export default Navbar;
