import React, { useState } from "react";

export default function BotoneraVertical(p_opciones = ["Opción 1", "Opción 2", "Opción 3", "Opción 4"]) {
  const [activo, setActivo] = useState(-1);
  const opciones = p_opciones;

  return (
    <div className="flex flex-col gap-2 w-64">
      {p_opciones.map((label, i) => (
        <button
          key={label}
          onClick={() => setActivo(i)}
          aria-pressed={activo === i}
          className={[
            "w-full px-4 py-3 rounded-md font-semibold transition-colors",
            activo === i
              ? "bg-duocamarillo text-duocgris"
              : "bg-duocgris text-white hover:bg-duocamarillo hover:text-duocgris",
          ].join(" ")}
        >
          {label}
        </button>
      ))}
    </div>
  );
}
