import React from "react";
import userImage from "../assets/user.png"; // ← reemplazá con la ruta de tu imagen local

function Header() {
  return (
    <header className="flex items-center justify-between px-6 py-3 bg-white border-b-2 border-black">
      {/* Texto a la izquierda */}
      <h1 className="text-xl font-semibold text-gray-800">
        Bienvenido, <span className="text-blue-600">Alumno</span>
      </h1>

      {/* Imagen a la derecha */}
      <img
        src={userImage}
        alt="Usuario"
        className="w-12 h-12 rounded-full object-cover"
      />
    </header>
  );
}

export default Header;
